const stats = ref([
  {
    id: 1,
    label: 'Active Products',
    target: 120808,
    currentValue: 0,
    iconColor: 'text-blue-400',
    svg: Diamond
  },
  {
    id: 2,
    label: 'Added Today',
    target: 159,
    currentValue: 0,
    iconColor: 'text-green-400',
    svg: AddedNew
  },
  {
    id: 3,
    label: 'Free Assets',
    target: 16246,
    currentValue: 0,
    iconColor: 'text-purple-400',
    svg: Free
  },
  {
    id: 4,
    label: 'Active Creators',
    target: 12917,
    currentValue: 0,
    iconColor: 'text-red-400',
    svg: Crown
  }
])

// stronger easing (very pronounced slow-down at end)
const easeOutQuint = (t) => 1 - Math.pow(1 - t, 5)

const animateStat = (stat, duration = 2000) => {
  // Single continuous rAF loop using ease-out so progress slows smoothly at the end.
  return new Promise((resolve) => {
    const start = performance.now()
    const target = stat.target

    const step = (now) => {
      const elapsed = now - start
      const t = Math.min(elapsed / duration, 1)
      const eased = easeOutQuint(t)
      const value = Math.floor(target * eased)

      if (value !== stat.currentValue) stat.currentValue = value

      if (t < 1) {
        requestAnimationFrame(step)
      } else {
        stat.currentValue = target
        resolve()
      }
    }

    requestAnimationFrame(step)
  })
}

onMounted(() => {
  // Start all counters simultaneously but give each a different duration
  // so the smallest stops first (~5s), the next ~3s later, then 1s gaps.
  const firstEnd = 2500 // ms
  const secondOffset = 500 // ms after first
  const followingOffset = 1000 // ms between subsequent finishes

  // Sort stats by target ascending to determine which should finish first
  const sorted = [...stats.value].sort((a, b) => a.target - b.target)

  const endTimes = sorted.map((s, i) => {
    if (i === 0) return firstEnd
    if (i === 1) return firstEnd + secondOffset
    return firstEnd + secondOffset + (i - 1) * followingOffset
  })

  // Start all animations at once with their respective durations
  sorted.forEach((s, i) => {
    const original = stats.value.find((st) => st.id === s.id)
    if (original) animateStat(original, Math.max(endTimes[i], 300))
  })
})
</script>

